mapper.update:
update tb_home_request
url = #{url,jdbcType=VARCHAR}, type = #{type,jdbcType=INTEGER}, typeName = #{typeName,jdbcType=VARCHAR}, userId = #{userId,jdbcType=BIGINT}, msg = #{msg,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, isdel = #{isdel,jdbcType=INTEGER}, reason = #{reason,jdbcType=VARCHAR}, createTime = #{createTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT thr.id,thr.typeName, thr.userId, thr.url, thr.reason, thr.msg, thr.status, tu.name AS userName FROM tb_home_request thr JOIN tb_user tu ON tu.id = thr.userId WHERE 1=1
AND thr.userId = #{userId, jdbcType=BIGINT}
AND thr.status = #{status, jdbcType=INTEGER}
AND thr.isdel = 0 ORDER BY thr.createTime DESC LIMIT #{page}, 10
